"use strict";
// Tipos de variaveis no TypeScript
// boolean
let isOpen;
isOpen = true;
console.log(isOpen);
// String ('foo', "foo", `foo`)
let message1, message2, message3;
message1 = 'Wanderson';
message2 = "Almeida";
message3 = `${message1} ${message2} Timóteo`;
console.log(message3);
// number -> funciona para int, floalt, double, hexadecimal
